window.addEventListener("DOMContentLoaded",(function(){openMenu(),animateHeader(),darkMode()}));let openMenu=()=>{document.querySelector(".burger-container").onclick=()=>{document.querySelector(".header").classList.toggle("navigation-open")}},animateHeader=()=>{let e=window.pageYOffset;document.addEventListener("scroll",()=>{let t=document.querySelector(".header"),n=window.pageYOffset;window.pageYOffset>50?t.classList.add("scroll"):t.classList.remove("scroll"),n>e?t.setAttribute("style","transform: translateY(calc(-100% + 0px)) translateZ(0px)"):t.setAttribute("style","transform: none"),e=n<=0?0:n})},darkMode=()=>{document.querySelector("input[name=theme-toggle]").addEventListener("change",(function(){this.checked?document.documentElement.setAttribute("data-theme","dark"):document.documentElement.setAttribute("data-theme","light");let e=document.querySelector(".header");e.classList.contains("navigation-open")&&setTimeout(()=>e.classList.remove("navigation-open"),300)}))};