window.addEventListener('load', function () {
	const e = document.querySelector('input[name=theme-toggle]')
	let t = e => (e ? 'dark' : 'light'),
		n = localStorage.getItem('theme'),
		o =
			window.matchMedia &&
			window.matchMedia('(prefers-color-scheme: dark)').matches
	;(e.checked = n ? 'dark' === n : o),
		document.documentElement.setAttribute('data-theme', t(e.checked)),
		e.addEventListener('change', function () {
			document.documentElement.setAttribute('data-theme', t(this.checked)),
				localStorage.setItem('theme', t(this.checked)),
				setTimeout(
					() =>
						document
							.querySelector('.header')
							.classList.remove('navigation-open'),
					300
				)
		})
}),
	window.addEventListener('load', function () {
		let e = window.pageYOffset
		document.addEventListener('scroll', () => {
			let t = document.querySelector('.header'),
				n = window.pageYOffset
			window.pageYOffset > 50
				? t.classList.add('scroll')
				: t.classList.remove('scroll'),
				n > e
					? t.setAttribute(
							'style',
							'transform: translateY(calc(-100% + 0px)) translateZ(0px)'
					  )
					: t.setAttribute('style', 'transform: none'),
				(e = n <= 0 ? 0 : n)
		})
	}),
	window.addEventListener('DOMContentLoaded', function () {
		document.querySelector('.burger-container').onclick = () => {
			document.querySelector('.header').classList.toggle('navigation-open')
		}
	})
